import React, { useState } from "react";
import {
  VideoCameraIcon,
  SparklesIcon,
} from "@heroicons/react/outline";
import ReactPlayer from "react-player";
import VideoDropZone from "./videoDropZone.jsx";
import VideoEditor from "./videoEditor.jsx";
import axios from 'axios';

const Process = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);

  // Metadata fields for the video
  const [videoMetadata, setVideoMetadata] = useState({
    videoDuration: null,
    processingTime: null,
    cutsMade: null,
    requestId: null,
    originalFilePath: null,
    editedFilePath: null,
    silenceDetails: [],
    fileSize: null,
  });

  const handleFileChange = (file) => {
    setSelectedVideo(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedVideo) {
      setErrorMessage("Please select a video file.");
      return;
    }

    const videoObject = videos.find((video) => video.url === selectedVideo);
    if (!videoObject) {
      setErrorMessage("Selected video not found. Please try again.");
      return;
    }

    const formData = new FormData();
    formData.append("name", videoObject.file);
    formData.append('requestId', videoMetadata.requestId); // Assuming requestId is needed

    setIsProcessing(true);
    setErrorMessage(null);

    try {
      const response = await fetch("http://localhost:5000/api/videos/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Failed to process the video.");
      }

      const data = await response.json();
      setProcessedVideo(data.editedFilePath); // Store the URL of the processed video
      setVideoMetadata({
        videoDuration: data.videoDuration,
        processingTime: data.processingTime,
        cutsMade: data.cutsMade,
        requestId: data.requestId,
        originalFilePath: data.originalFilePath,
        editedFilePath: data.editedFilePath,
        silenceDetails: data.silenceDetails,
        fileSize: data.fileSize,
      });
    } catch (error) {
      setErrorMessage('Error processing video: ' + error.message);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="flex">
      <div className="w-1/4 p-4">
        <VideoDropZone onFilesAdded={handleFileChange} />
        {selectedVideo && (
          <div className="mt-4">
            <ReactPlayer url={URL.createObjectURL(selectedVideo)} controls />
            <form onSubmit={handleSubmit} className="mt-4">
              <button
                type="submit"
                disabled={isProcessing}
                className="block w-full p-2 bg-blue-500 text-white rounded mt-4"
              >
                {isProcessing ? 'Processing...' : 'Process Video'}
              </button>
            </form>
            {errorMessage && (
              <div className="mt-4 text-red-500">
                <p>{errorMessage}</p>
              </div>
            )}
            {processedVideo && (
              <div className="mt-4">
                <h2 className="text-xl font-semibold">Processed Video</h2>
                <video controls src={processedVideo} className="mt-4 w-full" />
              </div>
            )}
          </div>
        )}
      </div>
      <div className="w-3/4 p-4">
        <VideoEditor video={selectedVideo} />
        {/* Add any additional components or content here */}
      </div>
    </div>
  );
};

export default Process;
