import React, { useState } from "react";
import {
  VideoCameraIcon,
  SparklesIcon,
} from "@heroicons/react/outline";
import ReactPlayer from "react-player";
import VideoDropZone from "./videoDropZone.jsx";
import VideoEditor from "./videoEditor.jsx";
import axios from 'axios';

const Process = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [processedVideo, setProcessedVideo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);

  // Inputs for silence detection
  const [noiseLevel, setNoiseLevel] = useState("-40");
  const [silenceDuration, setSilenceDuration] = useState("2");

  // Metadata fields for the video
  const [videoMetadata, setVideoMetadata] = useState({
    videoDuration: null,
    processingTime: null,
    cutsMade: null,
    requestId: null,
    originalFilePath: null,
    editedFilePath: null,
    silenceDetails: [],
    fileSize: null,
  });

  const handleFileChange = (file) => {
    setSelectedVideo(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!selectedVideo) {
      setErrorMessage("Please select a video file.");
      return;
    }

    const formData = new FormData();
    formData.append('video', selectedVideo);
    formData.append('noiseLevel', noiseLevel);
    formData.append('silenceDuration', silenceDuration);

    setIsProcessing(true);
    setErrorMessage(null);

    try {
      const response = await axios.post('/api/videos/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setProcessedVideo(response.data);
      setVideoMetadata({
        videoDuration: response.data.videoDuration,
        processingTime: response.data.processingTime,
        cutsMade: response.data.cutsMade,
        requestId: response.data.requestId,
        originalFilePath: response.data.originalFilePath,
        editedFilePath: response.data.editedFilePath,
        silenceDetails: response.data.silenceDetails,
        fileSize: response.data.fileSize,
      });
    } catch (error) {
      setErrorMessage('Error processing video: ' + error.response.data.message);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div>
      <VideoDropZone onFilesAdded={handleFileChange} />
      {selectedVideo && (
        <div>
          <ReactPlayer url={URL.createObjectURL(selectedVideo)} controls />
          <form onSubmit={handleSubmit}>
            <input
              type="number"
              value={noiseLevel}
              onChange={(e) => setNoiseLevel(e.target.value)}
              placeholder="Noise Level"
            />
            <input
              type="number"
              value={silenceDuration}
              onChange={(e) => setSilenceDuration(e.target.value)}
              placeholder="Silence Duration"
            />
            <button type="submit" disabled={isProcessing}>
              {isProcessing ? 'Processing...' : 'Process Video'}
            </button>
          </form>
          {errorMessage && <div className="error">{errorMessage}</div>}
          {processedVideo && (
            <div>
              <h3>Processed Video Details</h3>
              <p>Request ID: {videoMetadata.requestId}</p>
              <p>Original File Path: {videoMetadata.originalFilePath}</p>
              <p>Edited File Path: {videoMetadata.editedFilePath}</p>
              <p>Processing Time: {videoMetadata.processingTime} minutes</p>
              <p>Cuts Made: {videoMetadata.cutsMade}</p>
              <p>Silence Details: {JSON.stringify(videoMetadata.silenceDetails)}</p>
              <p>File Size: {videoMetadata.fileSize} bytes</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Process;
