import React, { useState } from 'react';
import { 
    VideoCameraIcon,
    SparklesIcon
} from "@heroicons/react/outline";
import ReactPlayer from 'react-player';
import VideoDropZone from './videoDropZone.jsx';
import VideoEditor from './videoEditor.jsx';

const Process = () => {
    const [videos, setVideos] = useState([]);
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [processedVideo, setProcessedVideo] = useState(null);
    const [isProcessing, setIsProcessing] = useState(false);
    const [errorMessage, setErrorMessage] = useState(null);

    // DB-Related State
    const [videoDetails, setVideoDetails] = useState(null); // For storing video metadata from the backend

    // User Inputs
    const [noiseLevel, setNoiseLevel] = useState('-20'); // Default noise level
    const [silenceDuration, setSilenceDuration] = useState('2'); // Default silence duration

    const handleFilesAdded = (files) => {
        const newVideos = files.map((file) => ({
            file,
            url: URL.createObjectURL(file),
        }));
        setVideos([...videos, ...newVideos]);
    };

    const handleSilenceVideo = async () => {
        if (!selectedVideo) {
            setErrorMessage('No video selected. Please select a video to process.');
            return;
        }

        const videoObject = videos.find(video => video.url === selectedVideo);
        if (!videoObject) {
            setErrorMessage('Selected video not found. Please try again.');
            return;
        }

        const formData = new FormData();
        formData.append('video', videoObject.file);
        formData.append('noiseLevel', noiseLevel);
        formData.append('silenceDuration', silenceDuration);

        setIsProcessing(true);
        setErrorMessage(null);

        try {
            const response = await fetch('http://localhost:5000/api/videos/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('Failed to process the video.');
            }

            const data = await response.json();

            setProcessedVideo(data.editedFile); // Backend provides processed video file path
            setVideoDetails(data); // Store all details from the backend response
        } catch (error) {
            setErrorMessage('An error occurred while processing the video. Please try again.');
        } finally {
            setIsProcessing(false);
        }
    };

    return (
        <div>
            <div className="bg-customBlue">Logo Should Be Here</div>
            <div className="relative flex flex-row items-start space-y-4 p-4">
                <div className="flex bg-zinc-50">
                    {/* Sidebar */}
                    <div className="flex flex-col items-center space-y-4">
                        {[
                            { icon: VideoCameraIcon, label: 'Video Silence Remover' },
                            { icon: SparklesIcon, label: 'Effects' },
                        ].map(({ icon: Icon, label }, index) => (
                            <div key={index} className="flex flex-col items-center space-y-1">
                                <Icon className="h-6 w-6 text-gray-500" />
                                <span className="text-gray-500">{label}</span>
                            </div>
                        ))}
                    </div>

                    <div className="flex flex-col m-2 pl-10 border-l-4 border-gray-300">
                        <h1 className="text-2xl font-bold mb-4">Video Upload</h1>
                        <VideoDropZone onFilesAdded={handleFilesAdded} />

                        {videos.length > 0 && (
                            <div className="mt-4">
                                <h2 className="text-xl font-semibold">Uploaded Videos</h2>
                                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
                                    {videos.map((video, index) => (
                                        <button 
                                            key={index} 
                                            className="relative rounded-lg overflow-hidden hover:cursor-pointer"
                                            onClick={() => setSelectedVideo(video.url)} 
                                        >
                                            <ReactPlayer url={video.url} controls width="100%" height="auto" className="rounded-lg" />
                                        </button>
                                    ))}
                                </div>
                            </div>
                        )}

                        {selectedVideo && (
                            <div className="mt-4">
                                <hr className="border-2 border-gray-300 shadow-lg" />
                                <VideoEditor url={selectedVideo} />
                                <div className="mt-4 flex">
                                    <label className="block text-gray-700">Noise Level (dB):</label>
                                    <select 
                                        value={noiseLevel} 
                                        onChange={(e) => setNoiseLevel(e.target.value)}
                                        className="mt-2 p-2 border border-gray-300 rounded"
                                    >
                                        {Array.from({ length: 81 }, (_, i) => -90 + i).map((value) => (
                                            <option key={value} value={value}>
                                                {value}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                <div className="mt-4 flex">
                                    <label className="block text-gray-700">Silence Duration (seconds):</label>
                                    <select 
                                        value={silenceDuration} 
                                        onChange={(e) => setSilenceDuration(e.target.value)}
                                        className="mt-2 p-2 border border-gray-300 rounded"
                                    >
                                        {Array.from({ length: 5 }, (_, i) => i + 1).map((value) => (
                                            <option key={value} value={value}>
                                                {value}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                                <button 
                                    className="mt-4 p-2 bg-red-500 text-white rounded"
                                    onClick={handleSilenceVideo}
                                    disabled={isProcessing}
                                >
                                    {isProcessing ? 'Processing...' : 'Silence Video'}
                                </button>
                            </div>
                        )}

                        {processedVideo && (
                            <div className="mt-4">
                                <h2 className="text-xl font-semibold">Processed Video</h2>
                                <video controls src={processedVideo} className="mt-4 w-full" />
                                {videoDetails && (
                                    <div className="mt-4">
                                        <h3 className="text-lg font-semibold">Video Details</h3>
                                        <ul>
                                            <li><strong>Original File Path:</strong> {videoDetails.originalFilePath}</li>
                                            <li><strong>Edited File Path:</strong> {videoDetails.editedFilePath}</li>
                                            <li><strong>Duration Removed:</strong> {videoDetails.durationRemoved} seconds</li>
                                            <li><strong>Cuts Made:</strong> {videoDetails.cutsMade}</li>
                                            <li><strong>Processing Time:</strong> {videoDetails.processingTime} seconds</li>
                                            <li><strong>Request ID:</strong> {videoDetails.requestId}</li>
                                        </ul>
                                    </div>
                                )}
                            </div>
                        )}

                        {errorMessage && (
                            <div className="mt-4 text-red-500">
                                <p>{errorMessage}</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Process;
