import React, { useState } from 'react';
import './process.css'; // Assuming you have custom styling here

const Process = () => {
    const [uploadedFile, setUploadedFile] = useState(null);
    const [isProcessing, setIsProcessing] = useState(false);
    const [videoDetails, setVideoDetails] = useState([]);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        setUploadedFile(file);
    };

    const handleProcessClick = () => {
        if (!uploadedFile) return;

        setIsProcessing(true);

        // Simulate processing and updating video details
        setTimeout(() => {
            setIsProcessing(false);
            setVideoDetails((prevDetails) => [
                ...prevDetails,
                {
                    name: uploadedFile.name,
                    creationDate: new Date().toLocaleDateString(),
                    status: 'completed',
                    editedFile: 'Download',
                    durationRemoved: '57.54',
                    cutsMade: 33,
                    requestId: Math.random().toString(36).substring(2, 10),
                },
            ]);
        }, 3000); // Mock 3 seconds processing time
    };

    return (
        <div className="process-container">
            <h1 style={{ color: 'var(--customColor)' }}>Video Silence Remover</h1>

            <div className="upload-section">
                <input 
                    type="file" 
                    accept="video/*" 
                    onChange={handleFileChange} 
                />
                <button
                    style={{ backgroundColor: 'var(--customColor)' }}
                    onClick={handleProcessClick}
                    disabled={!uploadedFile || isProcessing}
                >
                    {isProcessing ? 'Processing...' : 'Process File'}
                </button>
            </div>

            {isProcessing && <p>Processing in progress...</p>}

            {videoDetails.length > 0 && (
                <div className="video-details-section">
                    <h2>Your Files:</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Video Name</th>
                                <th>Creation Date</th>
                                <th>Status</th>
                                <th>Edited File</th>
                                <th>Silent Duration Removed</th>
                                <th>Cuts Made</th>
                                <th>Request ID</th>
                            </tr>
                        </thead>
                        <tbody>
                            {videoDetails.map((detail, index) => (
                                <tr key={index}>
                                    <td>{detail.name}</td>
                                    <td>{detail.creationDate}</td>
                                    <td>{detail.status}</td>
                                    <td>
                                        <button
                                            style={{ backgroundColor: 'var(--customColor)' }}
                                            onClick={() => alert('Downloading...')}
                                        >
                                            {detail.editedFile}
                                        </button>
                                    </td>
                                    <td>{detail.durationRemoved}</td>
                                    <td>{detail.cutsMade}</td>
                                    <td>{detail.requestId}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default Process;
